// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract ChainConstants {
    string constant public ERC712_VERSION = "1";

    uint256 constant public ROOT_CHAIN_ID = 1;
    bytes constant public ROOT_CHAIN_ID_BYTES = abi.encodePacked(uint(1));

    uint256 constant public CHILD_CHAIN_ID = 137;
    bytes constant public CHILD_CHAIN_ID_BYTES = abi.encodePacked(uint(137));
}
