// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

interface IPermissions {

    event SetPermission(uint8 action, address indexed company, address indexed nftAddress, bool allow);
    event SetCompaniesContract(address indexed companiesContract);

    function setCompaniesContract(address _companies) external;

    function setPermission(uint8 _action, address _company, address _nftAddress, bool _allow) external;

    function setPermissions(uint8[] memory _permissions, address _company, address _nftAddress, bool _allow) external;

    function setBoolPermissions(bool[16] memory _permissions, address _company, address _nftAddress) external;

    /** Getters */
    function getPermission(uint8 _action, address _company, address _nftAddress, address _signer) external view returns (bool);

    function getPermissions(address _company, address _nftAddress) external view returns(bool[] memory);

    function isSignerApproved(address _company, address _signer) external view returns (bool);

    function getCompaniesContract() external view returns(address);

}
