const ERC20 = artifacts.require('STN20.sol');
const MultisigWallet = artifacts.require('MultisigWallet.sol')
const  ethers = require('ethers');


module.exports = function(deployer) {
  deployer.deploy(ERC20, ethers.utils.parseUnits("20000", 18)).then(async () => {
    const erc20 = await ERC20.deployed();
    await erc20.transferOwnership(MultisigWallet.address); 
  });
};
