/** This token is deployed in the ETH network where minting is called by MintableAssetProxy*/
/** https://docs.matic.network/docs/develop/ethereum-matic/mintable-assets/ */

const HDREC = artifacts.require('RootMintableERC20.sol');

const MintableAssetProxy = "0x37c3bfC05d5ebF9EBb3FF80ce0bd0133Bf221BC8"; //https://static.matic.network/network/testnet/mumbai/index.json

module.exports = async function(deployer) {
    await deployer.deploy(HDREC, "HydroREC Token", "HDREC", MintableAssetProxy);
};